#!/bin/sh
# Toggle screenControlHF
# DontUseFBInk
# Name: Toggle screenControl
# Author: Darkwhite
# Icon: data:image/png;base64,


if pidof screenControlHF > /dev/null; then
    echo "screenControlHF is running, stopping it..."
    kill $(pidof screenControlHF)
    iptables -D INPUT -p tcp --dport 6789 -j ACCEPT 2>/dev/null
elif pidof screenControl > /dev/null; then
    echo "screenControl is running, stopping it..."
    kill $(pidof screenControl)
    iptables -D INPUT -p tcp --dport 6789 -j ACCEPT 2>/dev/null
else
    echo "Starting screenControl..."
    ip_addr=$(ifconfig | grep -Eo 'inet (addr:)?([0-9]*\.){3}[0-9]*' \
              | grep -Eo '([0-9]*\.){3}[0-9]*' \
              | grep -v '127.0.0.1')
    echo "screenControl started on: http://$ip_addr:6789"

    if ! iptables -C INPUT -p tcp --dport 6789 -j ACCEPT 2>/dev/null; then
        iptables -A INPUT -p tcp --dport 6789 -j ACCEPT
    fi

    if [ -f /lib/ld-linux-armhf.so.3 ]; then
        /mnt/us/extensions/screenControl/screenControlHF &
    else
        /mnt/us/extensions/screenControl/screenControl &
    fi
    sleep 5
fi

exit 0
